<?php
 class ipbanModel extends Model {public function clear(){$this->where('expires_time <> 0 AND expires_time < '.time())->delete();}public function name_exists($name, $id=0){$pk =$this->getPk();$where ="name='" . $name . "'  AND ". $pk ."<>'" . $id . "'";$result =$this->where($where)->count($pk);if ($result){return true;}else {return false;}}}